import { Request, Response } from 'express';
import fs from 'fs/promises';
import path from 'path';
import puppeteer, { Browser } from 'puppeteer';
import PDFHelper from './pdfhelper';


const OUTPUT_DIR = path.resolve('./public/intel-report-summary');

export default async function mainFunction(
  reqs: Request,
  res: Response,
): Promise<void> {
  let browser: Browser | null = null;
  
  const req ={
  body: {
    pagesize: 5000,
    from_date: 1763404200,
    to_date: 1766102399,
    location_id: '',
    organization_id: 'c5fa2c40-91f4-11f0-9fd4-4d2e0cf88036',
    include_archived: true,
    selectedOrg: 'SAFERWATCH DEMO',
    startDate: '11/18/2025',
    endDate: '12/18/2025'
  },
    session: {
    vrToken: "eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJ1ZW1haWwiOiJyb2hpdEBzYWZlcndhdGNoYXBwLmNvbSIsImlhdCI6MTc2NTg4MTkyMSwiZXhwIjoxNzcxMDY1OTIxfQ.JUjEPADYbe_5-XJS7wwIcoYx81BJ-vvEG-hMmigN-6U",
    client_ip: "",
    is_public: false,
    isSuperAdmin: true,
    isLocationAdmin:true,
    locationDropdown:[{region_id:"123",region_name:"hello"}]
  },
};
  try {
    const objHelper = new PDFHelper();
    const htmlContent: string = await objHelper.generatePdf(req);

    const filename = `${Date.now()}.pdf`;
    const filePath = path.join(OUTPUT_DIR, filename);

    await fs.mkdir(OUTPUT_DIR, { recursive: true });

    browser = await puppeteer.launch({
      headless: true,
  args: ['--no-sandbox', '--disable-setuid-sandbox'],
    });

    const page = await browser.newPage();

    // Set HTML content
    await page.setContent(htmlContent, {
      waitUntil: 'networkidle0',
    });

    await page.pdf({
      path: filePath,
      format: 'A4',
      printBackground: true,
      margin: {
        top: '2.5cm',
        bottom: '2cm',
        left: '1.5cm',
        right: '1.5cm',
      },
      displayHeaderFooter: true,
      headerTemplate: `
        <div style="width:100%; text-align:center; font-size:10px;">
          <img style="width:200px;" src="data:image/png;base64,"/>
        </div>
      `,
      footerTemplate: `<div></div>`,
    });

    await browser.close();

 // region show in browser
    // res.setHeader('Content-Type', 'application/pdf');
    // res.setHeader('Content-Disposition', `inline; filename="${filename}"`);
    // res.sendFile(filePath);

    res.download(filePath, filename);

  } catch (error) {
    console.error('PDF generation failed:', error);

    if (browser) {
      await browser.close();
    }

    if (!res.headersSent) {
      res.status(500).json({
        status_code: 500,
        response: 'PDF generation failed',
      });
    }
  }
}

